// Import p5.js
import p5 from 'p5';

let particles = [];

const sketch = (p) => {
  p.setup = () => {
    p.createCanvas(p.windowWidth, p.windowHeight);
    for (let i = 0; i < p.windowWidth / 5; i++) {
      particles.push(new Particle());
    }
  };

  p.draw = () => {
    p.background('#16166B');
    for (let i = 0; i < particles.length; i++) {
      particles[i].createParticle();
      particles[i].moveParticle();
      particles[i].joinParticles(particles.slice(i));
    }
  };

  class Particle {
    constructor() {
      this.x = p.random(0, p.width);
      this.y = p.random(0, p.height);
      this.size = p.random(0, 5);
      this.speedX = p.random(0, 1);
      this.speedY = p.random(0, 1);
    }

    createParticle() {
      p.noStroke();
      p.fill('rgba(200, 169, 169, 0.5)');
      p.circle(this.x, this.y, this.size);
    }

    moveParticle() {
      if (this.x < 0 || this.x > p.width) this.speedX *= -1;
      if (this.y < 0 || this.y > p.height) this.speedY *= -1;
      this.x += this.speedX;
      this.y += this.speedY;
    }

    joinParticles(particles) {
      particles.forEach((element) => {
        let dis = p.dist(this.x, this.y, element.x, element.y);
        if (dis < 85) {
          p.stroke('rgba(200, 169, 169, 0.5)');
          p.line(this.x, this.y, element.x, element.y);
        }
      });
    }
  }

  p.mouseMoved = () => {
    for (let i = 0; i < particles.length; i++) {
      let distance = p.dist(p.mouseX, p.mouseY, particles[i].x, particles[i].y);
      if (distance < 60) {
        particles[i].speedX = (particles[i].x - p.mouseX) / 20;
        particles[i].speedY = (particles[i].y - p.mouseY) / 20;
      }
    }
  };
};

// Create a new p5 instance using the sketch
new p5(sketch);
export default  Animation
