/* global createCanvas, background, random, width, height, noStroke, fill, circle, dist, mouseX, mouseY, stroke, line */

import React, { useEffect } from 'react';

const BackgroundAnimation = () => {
  useEffect(() => {
    let canvas;
    let particles = [];

    const setup = () => {
      canvas = createCanvas(window.innerWidth, window.innerHeight);
      canvas.parent('background-animation');
      for (let i = 0; i < window.innerWidth / 5; i++) {
        particles.push(new Particle());
      }
    };

    const draw = () => {
      background('#16166B');
      for (let i = 0; i < particles.length; i++) {
        particles[i].createParticle();
        particles[i].moveParticle();
        particles[i].joinParticles(particles.slice(i));
      }
    };

    class Particle {
      constructor() {
        this.x = random(0, width);
        this.y = random(0, height);
        this.size = random(0, 5);
        this.speedX = random(0, 1);
        this.speedY = random(0, 1);
      }

      createParticle() {
        noStroke();
        fill('rgba(200, 169, 169, 0.5)');
        circle(this.x, this.y, this.size);
      }

      moveParticle() {
        if (this.x < 0 || this.x > width) this.speedX *= -1;
        if (this.y < 0 || this.y > height) this.speedY *= -1;
        this.x += this.speedX;
        this.y += this.speedY;
      }

      joinParticles(particles) {
        particles.forEach((element) => {
          let dis = dist(this.x, this.y, element.x, element.y);
          if (dis < 85) {
            stroke('rgba(200, 169, 169, 0.5)');
            line(this.x, this.y, element.x, element.y);
          }
        });
      }
    }

    const mouseMoved = () => {
      for (let i = 0; i < particles.length; i++) {
        let distance = dist(mouseX, mouseY, particles[i].x, particles[i].y);
        if (distance < 60) {
          particles[i].speedX = (particles[i].x - mouseX) / 20;
          particles[i].speedY = (particles[i].y - mouseY) / 20;
        }
      }
    };

    setup();

    // Clean up the p5.js canvas when the component unmounts
    return () => {
      canvas.remove();
    };
  }, []);

  return <div id="background-animation" />;
};

export default BackgroundAnimation;
